import React, { useState,useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './Login.css';

import axios from "axios";
// import './fontawesome.min.css';

const LoginForm = () => {
 const [passwordType, setPasswordType] = useState('password');
 const [username, setUsername] = useState('');
 const [password, setPassword] = useState('');
 const [email, setEmail] = useState('');
 const [msgVisible,setMsgVisible]=useState(true);
 const [msgLogin,setMsgLogin]=useState('');
 const [msgSignup,setMsgSignup]=useState('');



  const [showText, setShowText] = useState('SHOW');
  const [color, setColor] = useState('#222');
  const [isLogin, setIsLogin] = useState(true); // Added state for conditional rendering


  const navigate=useNavigate();
  
  useEffect(() => {
    return () => {
      setUsername('');
      setPassword('');
      setEmail('');
    };
  },[]);


  const handlePasswordVisibility = () => {
    if (passwordType === 'password') {
      setPasswordType('text');
      setShowText('HIDE');
      setColor('#3498db');
    } else {
      setPasswordType('password');
      setShowText('SHOW');
      setColor('#222');
    }
  };

  const toggleForm = () => {
    setIsLogin((prevIsLogin) => !prevIsLogin);
    setMsgVisible(true);
  };


  const handleAuthentication = async (e) => {
    e.preventDefault();  // Prevent default form submission
  
    try {
      if (isLogin=== true) {
        if (!email || !password) {
          console.error("Please enter both email and password");
          return;
        }
  
        const response = await axios.post('http://localhost:8080/user/login', {
          email,
          password
        });

        if(response.data==='Login successful!')
        {
          setMsgLogin('Login succesful!');
        }

        else{
          setMsgLogin('Invalid email or password');
        }
  
        console.log(response.data);
        setUsername('');
        setPassword('');
        setEmail('');
        // Use navigate instead of history
        navigate("/EventDetails");
        
      } else {
        if (!username || !email || !password) {
          console.error("Please enter all fields");
          return;
        }
  
        const response = await axios.post('http://localhost:8080/user/signup', {
          username,
          email,
          password
        });
       

        console.log(response.data);
        setMsgVisible(false);
        setIsLogin(false); 
        setUsername('');
        setPassword('');
        setEmail('');
        // Redirect to login page after successful signup
      }
    } catch (error) {
      console.error('error');
    }
  
  };
  

  return (
    <div className="bg-img">
      <div className="content">
        <header>{isLogin ? 'Login Form' : 'Signup Form'}</header>

        
      {!msgVisible && (
        <div className="message-container">
          {/* Display user information here */}
          <p>Sign-Up Succesfull Do Login</p>
        </div>
      )}

        <form onSubmit={handleAuthentication}>

        {isLogin ? null : ( // Conditionally render for signup
        <div className="field username">
          <span className="fa fa-user"></span>
          <input type="text" required placeholder="Username"                 
           value={username}
          onChange={(e) => setUsername(e.target.value)}
          />
        </div>
      )}

          <div className="field">
            <span className="fa fa-user"></span>
            <input type="text" required placeholder="Email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            />
          </div>


          <div className="field space">
            <span className="fa fa-lock"></span>
            <input
              type={passwordType}
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="pass-key"
              required
              placeholder="Password"
            />
            <span className="show" style={{ color }} onClick={handlePasswordVisibility}>
              {showText}
            </span>
          </div>
          <div className="pass">
            <a href="#">Forgot Password?</a>
          </div>
          <div className="field">
            <input type="submit" value={isLogin ? 'LOGIN' : 'SIGNUP'} />
          </div>
        </form>

        <div className="signup">
          {isLogin ? (
            <>
              Don't have an account?
              <a href="#" onClick={toggleForm}>
                Signup Now
              </a>
            </>
          ) : (
            <>
              Already have an account?
              <a href="#" onClick={toggleForm}>
                Login Now
              </a>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
